import React from 'react';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import home from '../images/home.svg';

const HomeButton = ({ destination }) => {
  const redirectTo = (dest) => (<Redirect path={ `http://localhost:3000/${destination}` } />);

  return (
  // <Link to={ `http://localhost:3000/${destination}` }>
  //   {/* // <Link to="localhost:3000/bebidas"> */}
    <img src={ home } alt="home" width="30px" onClick={ () => redirectTo(destination) } />
  // </Link>
  );
};

export default HomeButton;

HomeButton.propTypes = {
  destination: PropTypes.string.isRequired,
};
