import React, { useState, useContext } from 'react';
import AppContext from '../context/AppContext';

const SearchBar = () => {
  const baseSearchBar = {
    searchInput: '',
    ingredientRadio: false,
    nameRadio: false,
    firstLetterRadio: false,
  };

  const [searchState, setSearchState] = useState(baseSearchBar);
  const [setFoodApiResults] = useContext(AppContext);

  const handleClick = async () => {
    const { searchInput, ingredientRadio, nameRadio, firstLetterRadio } = searchState;
    if (ingredientRadio) {
      const results = await SERVICEAPI(searchInput);
      setFoodApiResults(results);
    }
    if (nameRadio) {
      const results = await SERVICEAPI(searchInput);
      setFoodApiResults(results);
    }
    if (firstLetterRadio) {
      const results = await SERVICEAPI(searchInput);
      setFoodApiResults(results);
    }
  };

  const handleChange = ({ target: { value, name } }) => {
    if (name === 'searchInput') setSearchState({ ...searchState, searchInput: value });
    if (name === 'ingredientRadio') {
      setSearchState({ ...searchState, ingredientRadio: !searchState.ingredientRadio });
    }
    if (name === 'nameRadio') {
      setSearchState({ ...searchState, nameRadio: !searchState.nameRadio });
    }
    if (name === 'firstLetterRadio') {
      setSearchState({ ...searchState, firstLetterRadio: !searchState.firstLetterRadio });
    }
  };
  const { searchInput, ingredientRadio, nameRadio, firstLetterRadio } = searchState;
  return (
    <form>
      <input
        placeholder="Buscar receita"
        data-testid="search-input"
        name="searchInput"
        value={ searchInput }
        onChange={ handleChange }
      />
      <label htmlFor="ingredientRadio">
        Ingrediente
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          name="ingredientRadio"
          value={ ingredientRadio }
          onChange={ handleChange }
        />
      </label>
      <label htmlFor="nameRadio">
        Nome
        <input
          data-testid="name-search-radio"
          type="radio"
          name="nameRadio"
          value={ nameRadio }
          onClick={ handleChange }
        />
      </label>
      <label htmlFor="firstLetterRadio">
        Primeira letra
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          name="firstLetterRadio"
          value={ firstLetterRadio }
          onChange={ handleChange }
        />
      </label>
      <button type="button" data-testid="exec-search-btn">Buscar</button>
    </form>
  );
};

export default SearchBar;
