const getFoodResults = async (radioButton, searchInput) => {
  let type = 'i';
  if (radioButton === 'ingredientRadio') type = 'i';
  if (radioButton === 'nameRadio') type = 's';
  if (radioButton === 'firstLetterRadio') type = 'f';
  console.log(radioButton, type);
  const url = `https://www.themealdb.com/api/json/v1/1/filter.php?${type}=${searchInput}`;
  const result = await fetch(url).then((response) => response.json());
  console.log(result);
  return result;
};

export default getFoodResults;
