import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../CSS/ProgressoBebidas.css';

const CheckBoxProgresso = ({ ingredient, index }) => {
  const [strikeThrough, setStrikeThrough] = useState(false);
  return (
    <label
      htmlFor={ index }
      key={ ingredient.name }
      className={ strikeThrough ? 'cross' : null }
    >
      {`${ingredient.name} ${ingredient.measure === null
        ? '' : ingredient.measure}`}
      <input
        id={ index }
        type="checkbox"
        data-testid={ `${index}-ingredient-step` }
        onClick={ () => setStrikeThrough(!strikeThrough) }
      />
    </label>
  );
};

CheckBoxProgresso.propTypes = {
  ingredient: PropTypes.shape({
    name: PropTypes.string.isRequired,
    measure: PropTypes.string.isRequired,
  }).isRequired,
  index: PropTypes.number.isRequired,
};

export default CheckBoxProgresso;
