import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import home from '../images/home.svg';

const HomeButton = ({ destination }) => {
  const [redirectOn, setRedirect] = useState(false);
  const redirectTo = () => (setRedirect(true));
  if (redirectOn) return <Redirect path="/" />;
  return (
    <button type="button" onClick={ redirectTo }>
      <img src={ home } alt="home" width="30px" />
    </button>
  );
};

export default HomeButton;

HomeButton.propTypes = {
  destination: PropTypes.string.isRequired,
};
