import React, { useState } from 'react';
import CheckBoxProgresso from '../components/CheckboxProgresso';
// import { AppContext } from '../context/AppContext';
import mockDrink from '../context/mockInfo';
import shareImg from '../images/shareIcon.svg';
import whiteHeartImg from '../images/whiteHeartIcon.svg';

const ProgressoBebidas = () => {
  const { strDrink,
    strCategory, strDrinkThumb, strInstructions, ingredientsAndMeasurements } = mockDrink;
  const [stepsFinished, setStepsFinished] = useState(0);
  const [isFinished, setIsFinished] = useState(false);
  return (
    <div>
      <img src={ strDrinkThumb } alt={ strDrink } data-testid="recipe-photo" />
      <h2 data-testid="recipe-title">{strDrink}</h2>
      <button type="button" data-testid="share-btn">
        <img src={ shareImg } alt="Compartilhar" />
      </button>
      <button type="button" data-testid="favorite-btn">
        <img src={ whiteHeartImg } alt="Favoritar" />
      </button>
      <p data-testid="recipe-category">{strCategory}</p>
      <div className="ingredient-steps">
        { ingredientsAndMeasurements.map((ingredient, index) => (
          <CheckBoxProgresso ingredient={ ingredient } index={ index } key={ index } />
        )) }
      </div>
      <p data-testid="instructions">{strInstructions}</p>
      <button type="button" data-testid="finish-recipe-btn" disabled={ !isFinished }>
        Finalizar Receita
      </button>
    </div>
  );
};

export default ProgressoBebidas;
